C++ Code for BINARY  SEARCH
#include<iostream>
using namespace std;

/*BINARY SEARCH*/

int binarysearch(int arr[], int size, int element)
{
    int low, mid, high ;
    low = 0;
    high = size-1 ;

    /*Keep SEARCHING until (low<=high)*/
while(low <= high)
{
    mid = ((low+high)/2);
    if(arr[mid] == element)
    {
        return mid;
    }
    if(arr[mid] < element)
    {
        low = mid+1;
    }
    else
    {
        high = mid-1 ;
    }
}
    /*End SEARCHING*/
return -1;
}


int main()
{
//    Sorted Array for BINARY SEARCH

